        ref={nameInputRef}></input>
        <label htmlFor='age'>Age</label>
        <input id='age' type='number' 
        ref={ageInputRef}></input>
        <Button type='submit'>Add User</Button> 

        {/* add makeup component Button  */}

        </form>
        </Card>


        </Wrapper>

    )
}

export default AddUser;
